function displayPosts(s){let e="";for(let t=0;t<s.length;t++)post=s[t],e+=`
        <div class="col-lg-6">
        <div class="card mb-4">
            <a href="#!"><img class="card-img-top"
                    src="https://dummyimage.com/700x350/dee2e6/6c757d.jpg"
                    alt="..."></a>
            <div class="card-body">
                <div class="small text-muted">${post.tags[0]}</div>
                <h2 class="card-title h4">${post.title}</h2>
                <p class="card-text">${post.body}</p>
                <a class="btn btn-primary" href="single-post.html">Read more →</a>
            </div>
        </div>
    </div>
        `;injectInHTML("posts_list",e)}function injectInHTML(t,s){document.getElementById(t).innerHTML=s}function getPosts(){fetch("https://dummyjson.com/posts").then(t=>t.json()).then(t=>{displayPosts(t.posts)}).catch(console.error)}function getBackgroundPrice(t){return backgroundColor=t<=100?"bg-dark text-light":"bg-light"}function getStars(t){let s="";return 1<=t&&t<2?s+="⭐":2<=t&&t<3?s+="⭐⭐":3<=t&&t<4?s+="⭐⭐⭐":4<=t&&t<5?s+="⭐⭐⭐⭐":5==t&&(s+="⭐⭐⭐⭐⭐"),s}fetch("https://dummyjson.com/products?limit=24").then(t=>t.json()).then(t=>{results=t.products,resultsDiv="";for(let t=0;t<results.length;t++){var s=getStars(results[t].rating),e=getBackgroundPrice(results[t].price);resultsDiv+=`
       <div class="col mb-5">
           <div class="card ${e} h-100">
               <img class="card-img-top"
                   src="${results[t].images[0]}"
                   alt="...">
               <!-- Product details-->
               <div class="card-body p-4">
                   <div class="text-center">
                       <!-- Product name-->
                       <h6 class="fw-bolder">${results[t].brand}</h6>
                       <h5 class="fw-bolder">${results[t].title}</h5>
                       <h6 class="fw-bolder">${s}</h6>
                       <!-- Product price-->
                       ${results[t].price} €
                   </div>
               </div>
               <!-- Product actions-->
               <div class="card-footer p-4 pt-0 border-top-0 bg-transparent">
                   <div class="text-center">
                       <a class="btn btn-outline-dark mt-auto" href="single-product.html">View Product</a>
                   </div>
               </div>
           </div>
       </div>
       `}injectInHTML("products_list",resultsDiv)}).catch(console.error),getPosts();